{{
  (() => {
    const tipo = TipoAcesso.selectedOptionValue?.trim().toLowerCase();
    const devices = devices_no_DB.data || [];

    if (devices_no_DB.isLoading) return [{ label: "Carregando...", value: "" }];
    if (!tipo) return [{ label: "Selecione o tipo primeiro", value: "" }];

    const filtrados = devices
      .filter(d => {
        const tipoDevice = String(d.type || "").trim().toLowerCase();
        return tipoDevice === tipo && d.value;
      })
      .map(d => ({ label: d.label, value: String(d.value).trim() }))
      .filter((v,i,a) => a.findIndex(t => t.value === v.value) === i); // remove dup

    return filtrados.length > 0
      ? filtrados
      : [{ label: `Nenhum ${tipo.toUpperCase()} dispon√≠vel`, value: "" }];
  })()
}}
